mapper.select:
select

  distinct


from company

  


  order by ${orderByClause}


  limit ${offset},${rows}

mapper.insert:
insert into company (id, symbol, stock_code, 
  stock_name, company_name, market_value, 
  circ_market_value, active_market_value, last_price
  )
values (#{id,jdbcType=BIGINT}, #{symbol,jdbcType=VARCHAR}, #{stockCode,jdbcType=VARCHAR}, 
  #{stockName,jdbcType=VARCHAR}, #{companyName,jdbcType=VARCHAR}, #{marketValue,jdbcType=DOUBLE}, 
  #{circMarketValue,jdbcType=DOUBLE}, #{activeMarketValue,jdbcType=DOUBLE}, #{lastPrice,jdbcType=DECIMAL}
  )

	insert into company (id, symbol, stock_code, 
    stock_name, company_name, market_value, 
    circ_market_value, active_market_value, last_price
    )
  values 
  
